import React, { useState } from  'react';
 
    
const UserForm = (props) => {
//     const [username, setUsername] = useState("");
//     const [lastname, setLastname] = useState("");
//     const [email, setEmail] = useState("");
//     const [password, setPassword] = useState("");  
//     const [conpass, setConpass] = useState("");  
    //    const [values, setValue] = useState({username: "", lastname:"", email:"", password:"", conpass:""})
       const [values, setValues] = useState({username: "",lastname:"", email: "",  conpass:"", password: "" });
//     const createUser = (e) => {
//         e.preventDefault();
//         const newUser = { username,lastname, email, password, conpass };
//         console.log("Welcome", newUser);
//     };
     const handleChange = event => {
        const { name, value } = event.target;
        setValues({
            ...values,
            [name]: value
          });
        };


     const handleSubmit = event => {
        event.preventDefault(values.username);
        // setErrors(validate(values));
        // setIsSubmitting(true);
        submit();
      };
    function submit(){

       console.log ("login successfully ") ;
        var  a = validateLogin(values) ;
        console.log(values.email)
    }


     function validateLogin(values) {
        let errors = values.email;
        console.log(errors)
        // if (!values.email) {
        // } else if (!/\S+@\S+\.\S+/.test(values.email)) {
        //   errors = "Email address is invalid";
        // }
        // if (!values.password) {
        //   errors.password = "Password is required";
        // } else if (values.password.length < 10) {
        //   errors.password = "Password needs to be more than 10 characters";
        // }
        return  values.email;
      }



    return(
    <div className="container">
    <div className="jumbotron bg-dark text-light">
        <h1><span className="text-warning"></span></h1>
      </div>
     
        <form  onSubmit = {handleSubmit}>
    
                 <div class="form-group">
                <label>First name: </label> 
                <input type="text" onChange={ handleChange }   class="form-control" />
            </div>
                <div class="form-group">
                <label>Last name: </label> 
                <input type="text" onChange={ handleChange }   class="form-control" />
            </div>
            <div class="form-group">
                <label>Email Address: </label> 
                <input type="text" onChange={ handleChange }   class="form-control" />
            </div>
                 <div class="form-group">
                <label>Password: </label>
                <input type="text" onChange={ handleChange }   class="form-control" />
            </div>

             <div class="form-group">
                <label>Confirmed Password: </label>
                <input type="text" onChange={handleChange }   class="form-control" />
            </div>

            <button type="submit" class="btn btn-primary">Submit</button>
         


        </form>

</div>

    );
};
    
export default UserForm;
